function StepViewModel(n,t){var i=this;i.title=n;i.top=t}function StepListViewModel(n){var t=this;t.steps=ko.observableArray([]);t.sendData=function(){t.steps.removeAll();$.ajax({method:"POST",url:n,data:{text:$("#step-submit-input").val()},dataType:"json",success:function(n){JSON.parse(n).forEach(function(n){t.steps.push(new StepViewModel(n.Title,n.Top))})}})}}$("#button-panel").find("button").each(function(){$(this).click(function(){var n=$("#step-submit-input"),t=n.caret().begin,i=n.val();n.val(i.substr(0,t)+$(this).html()+i.substr(t));n.focus();n.caret(t+1)})});